import { useParams } from "react-router-dom"
import React, { useEffect, useState } from 'react'
import { Descriptions, Space, Table, Tag } from 'antd'
import { Image } from 'antd';
import "./ItemDetailContainer.css"

const apiKey = '702be93892c341a5aa64499ef9026d16';

const ItemDetailContainer = () => {

    const {game_id} = useParams ()
    const [game, setGame] = useState (null)

    useEffect (() => {

    fetch (`https://api.rawg.io/api/games/${game_id}?key=${apiKey}`)
        .then (api_answer => api_answer.json())
        .then (api_answer_json => {
            setGame(api_answer_json)
        })
        .catch (api_error => console.log(`Error!: ${api_error}`))
    
    }, [])

    let titulo, imagen1, imagen2, fecha_lanzamiento, descripcion, puntaje_metacritic, desarrolladores, plataformas, genero, precios

    if (game) {

        game.background_image? imagen1 = <div><Image preview={{mask: 'Click para ampliar imagen 1 del juego'}} height={400} src={game.background_image} /></div> : imagen1 =  <Image width={200} height={200} src="error" fallback="data:image/png;base64," />
        game.background_image_additional ? imagen2 = <div><Image preview={{mask: 'Click para ampliar imagen 2 del juego'}} height={400} src={game.background_image_additional} /></div>: imagen2 = <Image width={200} height={200} src="error" fallback="data:image/png;base64," />
        game.name ? titulo = <b>{game.name}</b> : titulo = '(Información no disponible)'
        game.released ? fecha_lanzamiento = game.released : fecha_lanzamiento = '(Información no disponible)'
        game.metacritic ? puntaje_metacritic = `${game.metacritic}/100` : puntaje_metacritic = '(Información no disponible)'
        game.developers.length > 0? desarrolladores = game.developers.map(desarrollador => desarrollador.name).join(' - '): desarrolladores = '(Información no disponible)'
        
        game.platforms.length > 0? 
        (
          plataformas = game.platforms.map(plataforma => plataforma.platform.name).join(' - ')
        )
        
        : 
        (plataformas = '(Información no disponible)',
        precios = '(Licencias no disponibles)')

        game.genres.length > 0? genero = game.genres.map(genero => genero.name).join(' - '): genero= '(Información no disponible)'
        game.description? descripcion = <div dangerouslySetInnerHTML={{__html: game.description}}></div> : descripcion = '(Información no disponible)'
    } else {
        titulo = genero = descripcion =  imagen1 = imagen2 = desarrolladores = plataformas = fecha_lanzamiento = puntaje_metacritic = <div className="spinner-border" role="status"></div>
    }

    const items = [
        {
          key: '1',
          label: 'Título original:',
          children: titulo
        },
        {
          key: '2',
          label: 'Fecha de lanzamiento:',
          children: fecha_lanzamiento,
        },
        {
          key: '3',
          label: 'Puntaje Metacritic:',
          children: puntaje_metacritic,
        },
        {
          key: '4',
          label: 'Desarrollador/es:',
          children: desarrolladores,
        },
        {
          key: '5',
          label: 'Disponible en plataformas',
          children: plataformas,
        },
        {
          key: '6',
          label: 'Categoría/s',
          children: genero,
        },
        {
          key: '7',
          label: 'Descripción (inglés)',
          children: descripcion
        },
    ]


    // precios = game.platforms.map(plataforma => <div>Versión {plataforma.platform.name}: &nbsp;${Math.floor(Math.floor(Math.random() * 6000) + 4000)}</div>)

    const buying_columns = [
      {
        title: 'Nombre completo',
        dataIndex: 'name',
        key: 'name',
      },

      {
        title: 'Plataforma',
        dataIndex: 'platform',
        key: 'platform',
      },

      {
        title: 'Tipo de juego',
        key: 'game_type',
        dataIndex: 'game_type',
        render: (_, { game_platforms }) => (
          <>
            {game_platforms.map((platform) => {
              let digital_platforms = ('playstation5', 'xbox-series-x', 'pc', 'ios')
              let color = digital_platforms.includes(platform) ? 'geekblue' : 'green';

              return (
                <Tag color={color} key={platform}>
                  {platform.toUpperCase()}
                </Tag>
              );
            })}
          </>
        ),
      },

      {
        title: 'Precio',
        dataIndex: 'price',
        key: 'price',
      },


      {
        title: 'Action',
        key: 'action',
        render: (_, record) => (
          <Space size="middle">
            <a>Invite {record.name}</a>
            <a>Delete</a>
          </Space>
        ),
      },
    ];














    const buying_data = [
      {
        name: 'John Brown',
        tags: ['nice', 'developer'],
        game_platforms: ['pc', 'psp']
      },
      {
        name: 'Jim Green',
        tags: ['loser'],
        game_platforms: ['pc', 'psp']
      },
      {
        name: 'Joe Black',
        tags: ['cool', 'teacher'],
        game_platforms: ['pc', 'psp']
      },
    ];

    return (

    <>
    <div className="d-flex justify-content-around">
      <div>{imagen1}</div>
      <div>{imagen2}</div>
    </div>
    <Descriptions bordered items={items} />

    <h1 className="texto_gamer">¡COMPRAR AHORA!</h1>

    <Table columns={buying_columns} dataSource={buying_data} />
    </>
    
    )}
    
export default ItemDetailContainer