import { useEffect, useState } from "react"
import { useParams } from "react-router-dom"
import {db} from "../../firebase/client"
import { doc, getDoc } from "firebase/firestore"
import { Descriptions, Image } from "antd"
import PeripheralBuy from "./peripheralBuy/peripheralBuy"

const PeripheralDetailContainer = () => {

    const {item_id} = useParams ()
    const [loading, setLoading] = useState(true)
    const [item, setItem] = useState(null)
    const my_product = doc(db, "products", item_id)

    useEffect ( () => {
        getDoc(my_product)
        .then (res => setItem(res.data()))
        .catch (error => console.error(error))
        .finally (() => setLoading(false))

    }, [])

    let title = <div className="spinner-border" role="status"></div>
    let stock = <div className="spinner-border" role="status"></div>
    let price = <div className="spinner-border" role="status"></div>
    let description = <div className="spinner-border" role="status"></div>
    let image = <div><Image width={200} height={200} src="error" fallback="data:image/png;base64," /></div>
    
    if (item!= null && !loading)

    {
        title = item.title
        stock = item.stock + ' unidades disponibles'
        price = <b>$ {item.price} + IVA</b>
        description = item.description
        image = <div><Image preview={{mask: 'Click para ampliar imagen de producto'}} height={400} src={item.image} /></div>
    }

    const items = [
        {
          key: '1',
          label: 'Producto:',
          children: title
        },
        {
          key: '2',
          label: 'Stock:',
          children: stock,
        },
        {
          key: '3',
          label: 'Precio:',
          children: price,
        },
        {
          key: '4',
          label: 'Descripci√≥n:',
          children: description,
        },
    ]    

    return (
        <>
          <div className="d-flex justify-content-center">
            <div>{image}</div>
          </div>
          <Descriptions bordered items={items} />

          {(item!= null && !loading) && <PeripheralBuy id={item_id} stock={item.stock} image={item.image} title={item.title} price={item.price}/>}
        </>
    )
}

export default PeripheralDetailContainer 